import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import Dashboard from './Dashboard';
import Users from './Users';
import Roles from './Roles';
import LoginPage from './LoginPage';
import Permissions from './Permissions';
import { Shield } from 'lucide-react';
import { fetchUsers, fetchRoles, fetchPermissions } from '../api';
import { AuthContext } from '..components/AuthContext';
import PrivateRoute from '..components/PrivateRoute';

function App() {
  const [users, setUsers] = React.useState([]);
  const [roles, setRoles] = React.useState([]);
  const [permissions, setPermissions] = React.useState([]);
  const { isAuthenticated } = React.useContext(AuthContext);

  React.useEffect(() => {
    const getData = async () => {
      try {
        const usersData = await fetchUsers();
        setUsers(usersData);

        const rolesData = await fetchRoles();
        setRoles(rolesData);

        const permissionsData = await fetchPermissions();
        setPermissions(permissionsData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    getData();
  }, []);

  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar />
      <div className="flex-1 overflow-auto">
        <nav className="bg-white shadow-sm">
          <div className="mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex h-16 justify-between items-center">
              <div className="flex items-center">
                <Shield className="h-8 w-8 text-indigo-600" />
                <h1 className="ml-2 text-xl font-semibold text-gray-900">Admin Dashboard</h1>
              </div>
              <div className="flex items-center">
                <button className="rounded-full bg-gray-800 p-1 text-gray-400 hover:text-white">
                  <span className="sr-only">View notifications</span>
                  {/* Add notification icon here */}
                </button>
                <div className="ml-4 flex items-center">
                  <img
                    className="h-8 w-8 rounded-full"
                    src="https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80"
                    alt="User avatar"
                  />
                </div>
              </div>
            </div>
          </div>
        </nav>
        <main className="p-6">
          <Routes>
            <Route path="/login" element={<LoginPage />} />
            <PrivateRoute path="/admin" element={<Dashboard users={users} roles={roles} permissions={permissions} />} />
            <PrivateRoute path="/users" element={<Users users={users} setUsers={setUsers} />} />
            <PrivateRoute path="/roles" element={<Roles roles={roles} setRoles={setRoles} />} />
            <PrivateRoute path="/permissions" element={<Permissions permissions={permissions} setPermissions={setPermissions} />} />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
